import CodeData from '../../data/codes.json'
import React from 'react'

class Coding extends React.Component {
  constructor(){
    super();
    this.state = {
        codes:CodeData
    }
  }

   selectCategory = () =>{
     this.setState({codes:'something'})
   }
  render(){
    console.log(this.state.codes)
    return <div>
    {Object.keys(this.state.codes).map(category => console.log(category))}
      <button onClick={this.selectCategory}>click to change</button>
    </div>
  }
}
export default Coding
