import './sigin.styles.css'
import { signInUserAuthWithEmailAndPassword } from "../../utilities/firebase.utility";
import { useState } from "react";
// import CustomButton from "../customButton/custom-button.component";
// import FormInput from "../Form-input/form-input.component";
const defaultFormFields = {
  email: "",
  password: "",
};
const SignInForm = () => {
  const [formFields, setFields] = useState(defaultFormFields);
  const { email, password } = formFields;
  const onSubmitHandle = async (event) => {
     event.preventDefault();
     try {
        const user = await signInUserAuthWithEmailAndPassword(email, password)
   
        console.log(user)
     } catch (error) {
        console.log(error.code)
     }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFields({
      ...formFields,
      [name]: value,
    });
  };
  return (
    <div className="sign-up-form">
      <h2>I already have an account</h2>
      <span>sign in with email and password</span>
      <form onSubmit={onSubmitHandle}>
        <input
          label="email"
          type="email"
          required
          value={email}
          onChange={handleChange}
          name="email"
        />
        <input
          label="password"
          type="password"
          required
          value={password}
          onChange={handleChange}
          name="password"
        />

        <div className="buttons-container">
          {" "}
              <button type="submit">SIGNIN</button>
        </div>
      </form>
    </div>
  );
};

export default SignInForm;
