import { useContext } from 'react'
import {Link } from "react-router-dom"
import {MenuContext} from '../../contexts/menu.context'
const Menu = () => {
    const {isOpen,SetIsOpen} = useContext(MenuContext)

   return (
      <div>
        {
            !isOpen &&(
                
         <div  className="overlay MyNAV">
   <a className="closebtn" onClick={()=>SetIsOpen(!isOpen)}>&times;</a>
   <div className="overlay-content">
     <span>About</span>
     <Link className="nav-link-container" to="/profile">
            <h3>MARKS</h3>
          </Link>
          <Link className="nav-link-container" to="/profile">
            <h3>UPDATE M</h3>
          </Link>
          <Link className="nav-link-container" to="/profile">
            <h3>MARKS</h3>
          </Link>
     <h3>About</h3>
     <h3>About</h3>
   </div>
         </div>
            )
        }
      </div>
   )
  
    

