import { createContext ,useState } from "react";

export const MenuContext = createContext({
   isOpen:false,
   marksOpen:false,
   updateMarksOpen:false,
   SetIsOpen:() => null,
   setMarksOpen:() => null,
   setUdateMarksOpen:() => null
   
})

export const MenuProvider  = ({children}) => {
    const [isOpen,SetIsOpen,setMarksOpen,setUdateMarksOpe,updateMarksOpen,marksOpen] = useState(null)
    const value = {isOpen,SetIsOpen,setMarksOpen,setUdateMarksOpe,updateMarksOpen,marksOpen}
    return <MenuContext.Provider value = {value}>{children}</MenuContext.Provider>
}
