import './look-up.styles.css'
import { useState } from 'react'
import Profile from '../profile/profile.component'
import Marks from '../marks/marks.component'
import Overview from '../overview/overview.component'
const defaultLookField = {
    lookUpUser:'',
    toggle:false
}
const LookUp = () => {
    const [LookUpFields,setFields] = useState(defaultLookField)
    const {lookUpUser,toggle} = LookUpFields
    const arr = ['19EM1A0501','19EM1A0502','19EM1A0503','19EM1A0503']
    const setUser = (user) =>{
        setFields({
            toggle:true,
            lookUpUser:user
        })
    }
    return (
        <div>
        {/* <ul className='look-up'> 
             
            <li onClick={() => setUser('19EM1A0501')}>19EM1A0501</li>
            <li>19EM1A0502</li>
            <li>19EM1A0503</li>
        </ul> */}
        {!toggle &&(
            arr.map(a=>{
                return <div>
                    <p onClick={()=>{setUser(a)}}>{a}</p>
                </div>
            })
        )
        }
        {toggle && (<div>
            <button className='back-button' onClick={() =>{setFields({...LookUpFields,toggle:false})}}>BACK</button>
            <h1 className='lookupuser'>ID -- {lookUpUser}</h1>
            <div>
                <Profile user = {lookUpUser}/>
                <Overview user = {lookUpUser} />
                <Marks user = {lookUpUser}/>

            </div>
        </div>)

        }
        </div>
      
    )
}


export default LookUp
