import CodeData from '../../data/codes.json'
import React from 'react'

class Coding extends React.Component {
  constructor(){
    super();
    this.state = {
        codes:CodeData['ARRAYS'],
        category:'ARRAYS'
    }
  }
   selectCategory = (category) =>{
     this.setState({codes:CodeData[category]}) 
     this.setState({category:category})
   }
  render(){
    const cod = this.state.codes
    return <div>  
      <button className="sem-button" onClick={()=>this.selectCategory('ARRAYS')}>ARRAYS</button>
      <button onClick={()=>this.selectCategory('STRINGS')}>STRINGS</button>
      <button onClick={()=>this.selectCategory('STACKS')}>STACKS</button>
      <button onClick={()=>this.selectCategory('GREEDY')}>GREEDY</button>
      <button onClick={()=>this.selectCategory('DYNAMIC-PROGRAMMING')}>DYNAMIC-PROGRAMMING</button>
      <div>
      <h3>{this.state.category}</h3>
        {  
            Object.keys(cod).map(questions => {
                const cod_s = cod[questions]
                return <div>
                    <p>{cod_s['title']}</p>
                    <p>{cod_s['q_link']}</p>
                    <p>{cod_s['s_link']}</p>
                </div>
            }
            )
        }
      </div>
    </div>
  }
}
export default Coding
