    <div className="square" style="--i:2;"></div>
    <div className="square" style="--i:3;"></div>
    <div className="square" style="--i:4;"></div>
    <div className="square" style="--i:5;"></div>
    
   <div className="container"> 
    <div className="form"> 
      <h2>LOGIN to CodePen</h2>
      <form action="">
        <div className="inputBx">
          <input type="text" required="required" /> 
          <span>Login</span>
          <i className="fas fa-user-circle"></i>
        </div>
        <div className="inputBx password">
          <input id="password-input" type="password" name="password" required/>
          <span>Password</span>
          <a href="#" className="password-control" onClick={console.log('not')}></a>
          <i className="fas fa-key"></i>
        </div>
        <label className="remember"><input type="checkbox" />
