        <div className="inputBx">
          <input type="submit" value="Log in" disabled /> 
        </div>
      </form>
      <p>Forgot password? <a href="#">Click Here</a></p>
      <p>Don't have an account <a href="#">Sign up</a></p>
    </div>
  </div>
    
  </div>
</section>

  );
};

