import { Outlet,Link } from "react-router-dom"
import { Fragment ,useContext} from "react"
import { UserContext } from "../../contexts/user.context"
import '../navigation/navigation.styles.css'
import { useNavigate} from 'react-router-dom';
import { MenuContext } from "../../contexts/menu.context";
const Navigation = () => {
    const navigate = useNavigate()
    const {CurrentUser,SetCurrentUser} = useContext(UserContext)
    const {SetIsOpen,isOpen} = useContext(MenuContext)
    const signOut = () => {
     console.log('user logged out')
     SetCurrentUser(null)
     navigate('/login')

  
    }
    return(
        <Fragment>
              <div className="navigation">
        <Link className="nav-link-container" to="/">
          <h1>HOME</h1>
        </Link>

        <div className="nav-links-container">
          <Link to="/shop" className="nav-link">
            Shop
          </Link>
          {CurrentUser ? (
            <span className="nav-link" onClick={signOut} >
              SIGNOUT
            </span>
           
          ) : (
            <Link to="/login" className="nav-link">
              SIGNIN
            </Link>
              )}
        
           </div>
         
      </div>
        {/* <div className="navigation">
          <Link className="" to="/profile">
            <h3>HOME</h3>
          </Link>
          {CurrentUser ? (
            <div className="nav-link-container">
        <h3 className="nav-link" onClick={signOut}> LOGOUT</h3>
        <h3 className="nav-link" onClick={()=>SetIsOpen(!isOpen)}>&#9776;</h3>
        </div>  
            ):
          ( <Link to="/login" className="nav-link">
              <h3>LOGIN</h3>
            </Link>)}      
             </div> */}
             
        <Outlet />
      </Fragment>
        
    )
}

export default Navigation
