import CodeData from '../../data/codes.json'
import React from 'react'

class Coding extends React.Component {
  constructor(){
    super();
    this.state = {
        codes:'nothing'
    }
  }

   selectCategory = (category) =>{
    
     this.setState({codes:category})
   }
  render(){
    console.log(this.state.codes)
    return <div>
      <h1>{this.state.codes}</h1>
      <button onClick={()=>this.selectCategory('ARRAYS')}>ARRAYS</button>
      <button onClick={()=>this.selectCategory('STRINGS')}>STRINGS</button>
      <button onClick={()=>this.selectCategory('STACKS')}>STACKS</button>
      <button onClick={()=>this.selectCategory('ARRAYS')}>GREEDY</button>
      <button onClick={()=>this.selectCategory('ARRAYS')}>DYNAMIC-PROGRAMMING</button>
    </div>
  }
}
export default Coding
