    //       {" "}
    //           <button type="submit">SIGNIN</button>
    //     </div>
    //   </form>
    // </div>
  

  
   <div className="container"> 
    <div className="form"> 
      <h2>LOGIN</h2>
      <form onSubmit={onSubmitHandle}>
        <div className="inputBx">
          <input 
           label="email"
          type="text"
          required
          value={email}
          onChange={handleChange}
          name="email"
           /> 
          <span>Login</span>
        </div>
        <div className="inputBx password">
          <input 
             label="password"
             type="password"
             required
             value={password}
             onChange={handleChange}
             name="password"

          />
          <span>Password</span>
