import { Outlet,Link } from "react-router-dom"
import { Fragment ,useContext} from "react"
import { UserContext } from "../../contexts/user.context"
import '../navigation/navigation.styles.css'
import { useNavigate} from 'react-router-dom';
import { MenuContext } from "../../contexts/menu.context";
const Navigation = () => {
    const navigate = useNavigate()
    const {CurrentUser,SetCurrentUser} = useContext(UserContext)
    const {SetIsOpen,isOpen} = useContext(MenuContext)
    const signOut = () => {
     console.log('user logged out')
     SetCurrentUser(null)
     navigate('/login')

  
    }
    return(
        <Fragment>{
          
        }
        <div className="navigation">
          <Link className="nav-link-container" to="/">
            <h3>HOME</h3>
          </Link>
          <Link className="nav-link-container" to="/profile">
            <h3>PROFILE</h3>
          </Link>
          <div className="nav-links-container">
          {CurrentUser ? (<h3 className="nav-link" onClick={signOut}> LOGOUT</h3>):
          ( <Link to="/login" className="nav-link">
              <h3>LOGIN</h3>
            </Link>)}      
             </div>

             <h3 className="nav-link-container menu-icon" onClick={()=>SetIsOpen(!isOpen)}>&#9776;</h3>
      
        </div>
        <Outlet />
      </Fragment>
        
    )
}

export default Navigation
