import './sigin.styles.css'
// import { signInUserAuthWithEmailAndPassword } from "../../utilities/firebase.utility";
import { useState,useContext } from "react";
import { UserContext } from '../../contexts/user.context';
import { useNavigate} from 'react-router-dom';
const defaultFormFields = {
  email: "",
  password: "",
};
const SignInForm = () => {
  const navigate = useNavigate()
  const [formFields, setFields] = useState(defaultFormFields);
  const { email, password } = formFields;
  const {SetCurrentUser,CurrentUser} = useContext(UserContext)
  const onSubmitHandle = async (event) => {
     event.preventDefault();
     try {
        // const user = await signInUserAuthWithEmailAndPassword(email, password)
        console.log(email,password)
        SetCurrentUser(email)
        navigate('/profile')
     } catch (error) {
        console.log(error.code)
     }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFields({
      ...formFields,
      [name]: value,
    });
  };
  return (
     <div classNameName="sign-up-form">
      <h2>I already have an account</h2>
      <span>sign in with email and password</span>
      <form onSubmit={onSubmitHandle}>
        <input
          label="email"
          type="text"
          required
          value={email}
          onChange={handleChange}
          name="email"
        />
        <input
          label="password"
          type="password"
          required
          value={password}
          onChange={handleChange}
          name="password"
        />

        <div classNameName="buttons-container">
          {" "}
              <button type="submit">SIGNIN</button>
        </div>
      </form>
    </div>
  )
