        }
     };

     render(){
        return(
            <div>
                <h3>{this.state.sem}</h3>
       <div>
        {
            Object.keys(semister).map(subjects => {
                return (
                    <div>
                        <h3>{subjects}</h3>
                        <input onChange={this.handleChange} name = {subjects}  placeholder={semister[subjects][0]}></input>
                    </div>
                )
            })
            
        }
        <button onClick={this.onSubmitHandle}>SUBMIT</button>
       </div>
            </div>
        )
     }
}

export default UpdateSem
